import { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import Notification from './components/Notification'


const App = () => {
  const [blogs, setBlogs] = useState([])
  const [newTitle, setNewTitle] = useState('')
  const [newUrl, setNewUrl] = useState('')
  const [newAuthor, setNewAuthor] = useState('')
  const [username, setUsername] = useState('') 
  const [password, setPassword] = useState('') 
  const [user, setUser] = useState(null)
  const [errorMessage, setErrorMessage] = useState(null)

  //EFFECT HOOKIT

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  // FORMIT

  const loginForm = () => (
    <form onSubmit={handleLogin}>
      <div>
        username
          <input
          type="text"
          value={username}
          name="Username"
          onChange={({ target }) => setUsername(target.value)}
        />
      </div>
      <div>
        password
          <input
          type="password"
          value={password}
          name="Password"
          onChange={({ target }) => setPassword(target.value)}
        />
      </div>
      <button type="submit">login</button>
    </form>      
  )

  const blogForm = () => (
    <div>
    <li>{loggedUser.name} logged in</li>
    <button onClick={() => window.localStorage.removeItem('loggedBlogappUser')}>
        log out EI AIHEUTA UUDELLEEN RENDERÃ–ITNIÃ„
        </button>
    
    <form onSubmit={addBlog}>
      <input
        title={newTitle}
        onChange={(e) => setNewTitle(newTitle)}
      />
      <input
        author={newAuthor}
        onChange={(e) => setNewTitle(newAuthor)}
      />
      <input
        title={newUrl}
        onChange={(e) => setNewTitle(newUrl)}
      />
      <button type="submit">save</button>
    </form>  
    </div>
  )

  

// APUFUNKTIOT


  const handleLogin = async (event) => {
    event.preventDefault()

    console.log('logging in with', username, password)
    try {
      const user = await loginService.login({
        username, password,
      })

      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      ) 

      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      setErrorMessage('wrong credentials')
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  }

  const addBlog = (event) => {
    event.preventDefault()
    const blogObject = {
      title: newTitle,
      author: newAuthor,
      url: newUrl
    }

    blogService
      .create(blogObject)
      .then(returnedBlog => {
        setBlogs(blogs.concat(returnedBlog))
        setNewTitle('')
        setNewAuthor('')
        setNewUrl('')
      })
  }

  
  const loggedUser = JSON.parse(window.localStorage.getItem('loggedBlogappUser'))

  // RENDERÃ–INTI
  
  return (
    <div>
      <Notification message={errorMessage} />
      {user === null ?
      loginForm() :
      blogForm()
    }

      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
      
    </div>
  )
}

export default App
