import { createSlice } from '@reduxjs/toolkit'
import blogsService from '../services/blogs'


const blogSlice = createSlice ({
  name: 'blogs',
  initialState: [],
  reducers: {

    like(state, action) {
      const id = action.payload.id
      const blogFind = state.find(n => n.id === id)
      const likeAdded = {
        ...blogFind, likes: blogFind.likes + 1
      }
      return state.map(blog =>
        (blog.id !== id ? blog : likeAdded )
      )
    },
    appendBlog(state, action) {
      state.push(action.payload)
    },
    setBlogs(state, action) {
      return action.payload
    },
    poista(state, action) {
        const id = action.payload.id
        return state.filter(blog =>
            blog.id !== id
          )

    }
  }
})

export const initializeBlogs = () => {
  return async dispatch => {
    const blogs = await blogsService.getAll()
    dispatch(setBlogs(blogs))
  }
}

export const selectAllBlogs = state =>  state.blogs

export const createBlog = content => {
  return async dispatch => {
    const newBlog = await blogsService.create(content)
    dispatch(appendBlog(newBlog))
  }
}

export const likeBlog = (id) => {
  return async dispatch => {
    const votable = await blogsService.addLike(id)
    dispatch(like(votable))
  }
}

export const deleteBlog = (id) => {
    return async dispatch => {
      const votable = await blogsService.deleteBlog(id)
      dispatch(poista(votable))
    }
  }



export const { setBlogs, like, appendBlog} = blogSlice.actions
export default blogSlice.reducer
