const showUser = ({loggedUser, setUser}) => {
    return (
    <div>    
    <div>
    <li>{loggedUser.name} logged in</li>
    <button onClick={() => { 
        setUser(null)
        window.localStorage.removeItem('loggedBlogappUser')}}>
        log out
    </button>
    </div>
    </div>    
    )}

export default showUser
