/* eslint-disable */
import { useState, useEffect, useRef } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import Notification from './components/Notification'
import BlogForm from './components/Blogform'
import LoginForm from './components/LoginForm'
import ShowUser from './components/showUser'
import Togglable from './components/Togglable'
import { useDispatch, useSelector } from 'react-redux'
import { setNotification } from './reducers/notificationReducer'
import { setBlogs } from './reducers/blogReducer'
import { initializeBlogs} from './reducers/blogReducer'
import { createBlog} from './reducers/blogReducer'
import { selectAllBlogs } from './reducers/blogReducer'
import { likeBlog, deleteBlogi } from './reducers/blogReducer'



const App = () => {
  //const [blogs, setBlogs] = useState([])
  const [newTitle, setNewTitle] = useState('')
  const [newUrl, setNewUrl] = useState('')
  const [newAuthor, setNewAuthor] = useState('')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const [loginVisible, setLoginVisible] = useState(false)
  const noteFormRef = useRef()

  //EFFECT HOOKIT
  const dispatch = useDispatch()

  useEffect(() => {
    blogService.getAll().then(() => {
      dispatch(initializeBlogs()) 
    })},[dispatch]) 

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  // FORMIT

  const loginForm = () => {
    const hideWhenVisible = { display: loginVisible ? 'none' : '' }
    const showWhenVisible = { display: loginVisible ? '' : 'none' }

    return (
      <div>
        <div style={hideWhenVisible}>
          <button onClick={() => setLoginVisible(true)}>log in</button>
        </div>
        <div style={showWhenVisible}>
          <LoginForm
            username={username}
            password={password}
            handleUsernameChange={({ target }) => setUsername(target.value)}
            handlePasswordChange={({ target }) => setPassword(target.value)}
            handleSubmit={handleLogin}
          />
          <button onClick={() => setLoginVisible(false)}>cancel</button>
        </div>
      </div>
    )
  }

  const blogForm = () => {

    return (
      <div>
        <Togglable buttonLabel="new note" ref={noteFormRef}>
          <BlogForm
            username={username}
            handleTitlechange={({ target }) => setNewTitle(target.value)}
            handleUrlchange={({ target }) => setNewUrl(target.value)}
            handleAuthorchange={({ target }) => setNewAuthor(target.value)}
            handleSubmit={addBlog}
            loggedUser={loggedUser}
            setUser={setUser}
            newTitle={newTitle}
            newAuthor={newAuthor}
            newUrl={newUrl}
          />
        </Togglable>
          <button onClick={() => setLoginVisible(false)}>cancel</button>
        </div>
    )
  }




  // APUFUNKTIOT


  /*
  const deleteBlog = id => {
    const blogs = useSelector(selectAllBlogs)
    blogService
      .poista(id)
      .then(setBlogs(blogs.filter(blog => blog.id !== id)))
  }
*/
  const handleLogin = async (event) => {
    event.preventDefault()

    console.log('logging in with', username, password)
    try {
      const user = await loginService.login({
        username, password,
      })

      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      )

      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      dispatch(setNotification('wrong credentials', 5))
    }
  }

  const addBlog = async (event) => {
    event.preventDefault()
    noteFormRef.current.toggleVisibility()
    const blogObject = {
      title: newTitle,
      author: newAuthor,
      url: newUrl,
      likes: 0
    }
    console.log(blogObject)

    dispatch(createBlog(blogObject))
    setNewTitle('')
    setNewAuthor('')
    setNewUrl('')
    dispatch(setNotification(`a new blog ${blogObject.title} by ${blogObject.author} was added`, 5))
  }


  const loggedUser = JSON.parse(window.localStorage.getItem('loggedBlogappUser'))

  const ShowBlogs = () => {
    const blogs = useSelector(selectAllBlogs)
    console.log(blogs)
    const sortedBlogs = [...blogs].sort((a, b) => parseFloat(b.likes) - parseFloat(a.likes))
    return ( sortedBlogs.map(blog =>
      <Blog key={blog.id} blog={blog} 
      addLike={() =>    
        dispatch(likeBlog(blog)) &
        dispatch(setNotification("you voted " + blog.title, 5))
          .catch(error => {
            dispatch(setNotification(
              `Blog '${blog.title}' was already removed from server`
              , 5))
            })} 
      deleteBlog={() => 
        dispatch(deleteBlogi(blog)) &
        dispatch(setNotification("you deleted " + blog.title, 5))} />
    ))
  }

  const ShowUsers = () => {
    const blogs = useSelector(selectAllBlogs)
    console.log(blogs)
  }
  // RENDERÃ–INTI


  return (
    <div>
      <div>
        <h1>blogs</h1>
      </div>
      <Notification />
      {user !== null ? <ShowUser loggedUser={loggedUser} setUser={setUser}/> : <p></p> }
      {user === null ?
        loginForm() :
        blogForm()
      }
      <div>
        { user ? <ShowBlogs />
         : <p></p> }
      </div>



    </div>
  )
}

export default App
