import { useState, useEffect, useRef } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import Notification from './components/Notification'
import BlogForm from './components/Blogform'
import LoginForm from './components/LoginForm'
import LoginForm2 from './components/LoginForm2'
import loginForm from './components/LoginForm2'
import ShowUser from './components/showUser'
import Togglable from './components/Togglable'
import { useDispatch, useSelector } from 'react-redux'
import { setNotification } from './reducers/notificationReducer'
import { setBlogs } from './reducers/blogReducer'
import { initializeBlogs} from './reducers/blogReducer'
import { createBlog} from './reducers/blogReducer'
import { selectAllBlogs } from './reducers/blogReducer'
import { likeBlog, deleteBlogi } from './reducers/blogReducer'
import userService from './services/users'
import BlogForm2 from './components/BlogForm2'
import {
  BrowserRouter as Router,
  Routes, Route, Link, useParams
} from "react-router-dom"
import './mystyle.css'



const App = () => {
  //const [blogs, setBlogs] = useState([])
  const [newTitle, setNewTitle] = useState('')
  const [newUrl, setNewUrl] = useState('')
  const [newAuthor, setNewAuthor] = useState('')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const [users, setUsers] = useState([])
  const [loginVisible, setLoginVisible] = useState(false)
  const noteFormRef = useRef()

  //EFFECT HOOKIT
  const dispatch = useDispatch()

  useEffect(() => {
    blogService.getAll().then(() => {
      dispatch(initializeBlogs()) 
    })},[dispatch]) 

    useEffect(() => {
      userService.getAll().then(users =>
        setUsers( users )
      )
    }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  // FORMIT


  // APUFUNKTIOT


  const handleLogin = async (event) => {
    event.preventDefault()

    console.log('logging in with', username, password)
    try {
      const user = await loginService.login({
        username, password,
      })

      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      )

      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      dispatch(setNotification('wrong credentials', 5))
    }
  }

  const addBlog = async (event) => {
    event.preventDefault()
    noteFormRef.current.toggleVisibility()
    const blogObject = {
      title: newTitle,
      author: newAuthor,
      url: newUrl,
      likes: 0
    }
    console.log(blogObject)

    dispatch(createBlog(blogObject))
    setNewTitle('')
    setNewAuthor('')
    setNewUrl('')
    dispatch(setNotification(`a new blog ${blogObject.title} by ${blogObject.author} was added`, 5))
  }


  const loggedUser = JSON.parse(window.localStorage.getItem('loggedBlogappUser'))


  const ShowBlogs = () => {
    const blogs = useSelector(selectAllBlogs)
    const sortedBlogs = [...blogs].sort((a, b) => parseFloat(b.likes) - parseFloat(a.likes))
    return (
      sortedBlogs.map(blog =>  <li key={blog.id}><Link to={`/api/blogs/${blog.id}`}>{blog.title} {blog.author}</Link></li>)
    )
  }

  const ShowBlogsPage = () => {
    return (
      <div>
        <BlogForm2     newTitle={newTitle} newAuthor={newAuthor} newUrl={newUrl} addBlog={addBlog}
      loggedUser={loggedUser} setUser={setUser} setNewAuthor={setNewAuthor} setNewTitle={setNewTitle}
    setNewUrl={setNewUrl} noteFormRef={noteFormRef} username={username}/>
        <ShowBlogs />
      </div>
    )
  }

  const ShowUser2 = (kayttaja) => {
    const df = kayttaja
    console.log("here", df)
    try {
    return (
